jQuery(document).ready(function(e){for(var i=[],n=[4,6,8,10],t=0;t<e(".bubbles").width();t++)i.push(t);function a(t){return t[Math.floor(Math.random()*t.length)]}setInterval(function(){var t=a(n);e(".bubbles").append('<div class="individual-bubble" style="left: '+a(i)+"px; width: "+t+"px; height:"+t+'px;"></div>'),e(".individual-bubble").animate({bottom:"100%",opacity:"-=0.7"},3e3,function(){e(this).remove()})},350)});